pragma solidity ^0.4.18;

import "./AragonApp.sol";
import "../common/IForwarder.sol";
import "../common/EVMCallScript.sol";

contract CounterFwd is AragonApp, IForwarder, EVMCallScriptRunner {
    bytes32 constant public ADMIN = bytes32(1);

    uint public ctr = 0;
    uint public threshold = 1;

    // Setters
    function increment() auth(ADMIN) { ctr++; }
    function decrement() auth(ADMIN) { require(ctr > 0); ctr--; }
    function setThreshold(uint256 x) auth(ADMIN) { threshold = x; }

    function isForwarder() public pure returns (bool) {
        return true;
    }

    /**
    * @dev Forwarder will forward iff counter is at least threshold
    *      Useful for testing logic around forwarders that can be toggled
    */
    function canForward(address _sender, bytes _evmCallScript) public constant returns (bool) {
        return ctr >= threshold;
    }

    function forward(bytes _evmCallScript) public {
        require(canForward(msg.sender, _evmCallScript));
        runScript(_evmCallScript);
    }
}
